SET PIPELINE_ID = ?;
SET PIPELINE_NAME = ?;
SET SUB_PROCESS_NAME = ?;
SET PROCESS_NAME = ?;
SET UTIL = ?;
SET DATAFACTORY = ?;
SET PROGRAM_LIST = $UTIL||'.PROGRAM_LIST';
SET PROCESS_LIST = $UTIL||'.PROCESS_LIST';
EXECUTE IMMEDIATE
$$
DECLARE
v_OBJECT_DB VARCHAR;
v_OBJECT_SCHEMA VARCHAR;
v_OBJECT_NAME VARCHAR; 
v_OBJECT_SIGNATURE VARCHAR;
v_STMT VARCHAR;
BEGIN
----------Get the meta data of the procedure and run it---------------------
SELECT 
OBJECT_DB, 
OBJECT_SCHEMA, 
OBJECT_NAME, 
OBJECT_SIGNATURE
INTO 
:v_OBJECT_DB, 
:v_OBJECT_SCHEMA, 
:v_OBJECT_NAME, 
:v_OBJECT_SIGNATURE

FROM IDENTIFIER($PROGRAM_LIST) PGM JOIN IDENTIFIER($PROCESS_LIST) PRC ON PGM.PROCESS_NAME = PRC.PROCESS_NAME AND PGM.DATA_FACTORY_NAME = PRC.DATA_FACTORY_NAME AND PRC.ACTIVE_INDC = 'Y'
WHERE UPPER(PGM.SUB_PROCESS_NAME) = $SUB_PROCESS_NAME AND UPPER(PGM.PROCESS_NAME) = $PROCESS_NAME AND PGM.DATA_FACTORY_NAME = $DATAFACTORY;

v_STMT := 'CALL '||:v_OBJECT_DB||'.'||:v_OBJECT_SCHEMA||'.'||:v_OBJECT_NAME||:v_OBJECT_SIGNATURE;

EXECUTE IMMEDIATE v_STMT;

RETURN v_STMT; 

END;
$$

;

